<template>
	<div class="login fixed-full">
		<v-touch tag="div" v-on:tap="close" class="close">取消</v-touch>
		<div class="login-m">
			<ul class="login-list">
				<li>
					<label>
						<span class="icon phone-icon"></span>
						<div class="inputbox">
							<input type="tel" ref="tel" placeholder="请输入手机号" v-model="phone"/>
						</div>
					</label>
				</li>
				<li>
					<label>
						<span class="icon lock-icon"></span>
						<div class="inputbox">
							<input ref="pwd" type="password" placeholder="请输入密码" v-model="pwd"/>
						</div>
					</label>
				</li>
			</ul>
			<v-touch tag="div" v-on:tap ="login" class="btn">立即登录</v-touch>
			<v-touch tag="div" class="wechat-login">
				<span class="wechat-icon"></span><span class="text">微信登录</span>
			</v-touch >
		</div>
	</div>
</template>

<script>
	export default {
		data () {
			return {
				phone : "",
				pwd: ""
			}
		},
		methods : {
			close () {
				this.inputBlur()
				this.$emit('login-say',false)
			},
			inputBlur () {
				this.$refs.tel.blur();
				this.$refs.pwd.blur();
			},
			login(){
				//登录post请求，获取用户id
				this.inputBlur()
				var	userId = "78cio_jk11"
				this.$emit('login',userId)
			}
		}
	}
</script>

